using Application.Common.Repositories;
using Infrastructure.Persistence;
using Infrastructure.Persistence.Collections;
using MongoDB.Driver;
using System.Globalization;

namespace Infrastructure.Repositories;

public class DefaultArticleRepository : IDefaultArticleRepository
{
    private DataContext _context;
    private string AuthorID { get; } = "AAAAAAAAAECAAAAAAAAAAA";

    public DefaultArticleRepository(DataContext context)
    {
        _context = context;
    }

    public void AddTermOfServiceAndPolicy()
    {

        var ID = new Guid("00000000-0000-4000-8000-000000000000");
        DateTime dt = DateTime.Parse("01/01/2024", new CultureInfo("us"));

        var newPost = new PostCollection()
        {
            ID = ID,
            Title = "นโยบายคุ้มครองข้อมูลส่วนบุคคล (Privacy Protection Policy)",
            Content = "{\"time\":1709367056035,\"blocks\":[{\"id\":\"g6kAq1t0cE\",\"type\":\"header\",\"data\":{\"text\":\"การเคารพสิทธิในความเป็นส่วนบุคคล\",\"level\":2}},{\"id\":\"nHA88-j7DD\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑ &nbsp; เบต้าบล็อกเคารพและให้ความสำคัญถึงสิทธิส่วนบุคคลและการคุ้มครองข้อมูลส่วนบุคคล และ เบต้าบล็อกตระหนักดีว่า ผู้ใช้บริการด้านการศึกษา ส่งเสริม ประยุกต์ และการพัฒนาวิชาการและวิชาชีพชั้นสูง การวิจัย พัฒนาและการถ่ายทอดเทคโนโลยี การให้บริการวิชาการและวิชาชีพแก่สังคม และการทะนุบำรุงศิลปะและวัฒนธรรม ตามพันธกิจของเบต้าบล็อก หรือบริการอื่นใดของเบต้าบล็อก ย่อมมีความประสงค์ที่จะได้รับการคุ้มครองข้อมูลส่วนบุคคล รวมถึงความมั่นคงปลอดภัยในการใช้บริการของเบต้าบล็อก\"}},{\"id\":\"h0g5YXvarL\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๒ &nbsp; ข้อมูลส่วนบุคคลที่เบต้าบล็อกได้รับมา เช่น ชื่อ อายุ ที่อยู่ อีเมล รูปภาพ บทความ ข้อมูลการใช้งานระบบเบต้าบล็อก เป็นต้น ซึ่งสามารถบ่งบอกตัวบุคคลของเจ้าของข้อมูลส่วนบุคคลนั้นได้ จะถูกนำไปใช้ให้เป็นไปตามวัตถุประสงค์การดำเนินงานของเบต้าบล็อกเท่านั้น และเบต้าบล็อกจะดำเนินมาตรการที่เข้มงวดในการรักษาความมั่นคงปลอดภัย ตลอดจนการป้องกันมิให้มีการนำข้อมูลส่วนบุคคลไปใช้โดยมิชอบ\"}},{\"id\":\"B7shdUEVqm\",\"type\":\"header\",\"data\":{\"text\":\"การเก็บรวบรวมข้อมูลส่วนบุคคลอย่างจำกัด\",\"level\":2}},{\"id\":\"ItnX7peU1o\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๓ &nbsp; เบต้าบล็อกจะจัดเก็บข้อมูลส่วนบุคคลไว้เท่าที่จำเป็น ภายใต้อำนาจหน้าที่และวัตถุประสงค์ในการดำเนินงานของเบต้าบล็อกตามที่กฎหมายกำหนด หรือรับรองตามมาตรา ๒๔ แห่งพระราชบัญญัติข้อมูลข่าวสารราชการ พ.ศ. ๒๕๔๐ เท่านั้น ในกรณีที่เบต้าบล็อกประสงค์จะใช้ข้อมูลส่วนบุคคลเพื่อวัตถุประสงค์อื่น เบต้าบล็อกจะแจ้งความประสงค์ให้เจ้าของข้อมูลส่วนบุคคลทราบและขอความยินยอมจากเจ้าของข้อมูลส่วนบุคคลนั้นก่อน เว้นแต่เป็นกรณีที่กฎหมาย ระเบียบ คำสั่ง ประกาศกำหนดให้กระทำได้โดยไม่ต้องขอความยินยอมจากเจ้าของข้อมูลส่วนบุคคลนั้นก่อน\"}},{\"id\":\"d5PrT7Ik6M\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๔ &nbsp; ในการเก็บรวบรวมข้อมูล และเก็บรักษาข้อมูลส่วนบุคคล เบต้าบล็อกจะใช้วิธีการที่ชอบด้วยกฎหมายและเป็นธรรมในการเก็บรวบรวมและจัดเก็บข้อมูล ตลอดจนเก็บรวบรวม และจัดเก็บข้อมูลส่วนบุคคลอย่างจำกัดเพียงเท่าที่จำเป็นแก่การดำเนินการของเบต้าบล็อกการให้บริการธุรกรรมทางอิเล็กทรอนิกส์ หรือบริการด้วยวิธีการทางอิเล็กทรอนิกส์อื่นใดภายใต้วัตถุประสงค์ของเบต้าบล็อกเท่านั้น\"}},{\"id\":\"eg0zIGbpqr\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๕ &nbsp; เบต้าบล็อกจะขอความยินยอมจากเจ้าของข้อมูลส่วนบุคคลก่อนทำการเก็บรวบรวม เว้นแต่\"}},{\"id\":\"eperyDaT-B\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๑) เพื่อจัดทำเอกสารประวัติศาสตร์หรือจดหมายเหตุของเบต้าบล็อก\"}},{\"id\":\"Wa_UycFVKm\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๒) เพื่อการศึกษาวิจัยหรือสถิติ ทั้งนี้ต้องสอดคล้องกับมาตรฐานจริยธรรมการวิจัยในมนุษย์\"}},{\"id\":\"_2La_lZtIZ\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๓) เพื่อป้องกันหรือระงับอันตรายต่อชีวิต ร่างกาย หรือสุขภาพของบุคคล\"}},{\"id\":\"LDTBOpmfxT\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๔) เพื่อการทำสัญญาหรือการปฏิบัติตามสัญญาระหว่างเบต้าบล็อกกับเจ้าของข้อมูลส่วนบุคคล อาทิ การจ้างบุคลากร การจัดการศึกษาสำหรับนักเรียนและนักศึกษา เพื่อดำเนินการเกี่ยวกับการบริหารจัดการให้บรรลุวัตถุประสงค์ด้านการจัดการศึกษา การวิจัย พัฒนาและถ่ายทอดเทคโนโลยี การบริการวิชาการและวิชาชีพแก่สังคม การทะนุบำรุงศิลปะและวัฒนธรรม\"}},{\"id\":\"6uIgUl6qcP\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๕) เพื่อการปฏิบัติตามกฎหมายต่าง ๆ ของเบต้าบล็อก\"}},{\"id\":\"c-eCDHMomE\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๖ &nbsp; เบต้าบล็อกจะไม่จัดเก็บข้อมูลส่วนบุคคลซึ่งเกี่ยวกับเชื้อชาติ เผ่าพันธุ์ ความคิดเห็นทางการเมือง ความเชื่อในลัทธิ ศาสนาหรือปรัชญา พฤติกรรมทางเพศ ประวัติอาชญากรรม ข้อมูลสุขภาพ ความพิการ ข้อมูลพันธุกรรม ข้อมูลชีวภาพ หรือข้อมูลอื่นใดที่อาจเป็นผลร้าย ทำให้เสียชื่อเสียง หรืออาจก่อให้เกิดความรู้สึกเกี่ยวกับการเลือกปฏิบัติโดยไม่เป็นธรรมหรือความไม่เท่าเทียมกันแก่บุคคลใด เว้นแต่\"}},{\"id\":\"JxAvGBjwo8\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๑) ได้รับความยินยอมจากเจ้าของข้อมูลส่วนบุคคล\"}},{\"id\":\"20p2NEL77a\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๒) เป็นกรณีที่กฎหมายกำหนด\"}},{\"id\":\"fKwC4pHLfG\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๓) เป็นไปเพื่อประโยชน์ของเจ้าของข้อมูลส่วนบุคคล และการขอความยินยอมไม่อาจกระทำได้ในเวลานั้น\"}},{\"id\":\"Uqiu6A-BEK\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๔) เป็นไปเพื่อประโยชน์เกี่ยวกับชีวิต สุขภาพ หรือความปลอดภัย\"}},{\"id\":\"rhYpxKl0SQ\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๕) เพื่อประโยชน์ในการศึกษา วิจัย หรือการจัดทำสถิติ\"}},{\"id\":\"sUTX4p2eIc\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๗ &nbsp; เบต้าบล็อกอาจรวบรวมข้อมูลส่วนบุคคลเข้ากับข้อมูลส่วนบุคคลที่ได้รับมาจากแหล่งอื่นเฉพาะในกรณีที่มีความจำเป็นและได้รับความยินยอมจากเจ้าของข้อมูลส่วนบุคคล หรือเป็นการยินยอมจากแหล่งอื่นให้แลกเปลี่ยนข้อมูลกับเบต้าบล็อกขอนแก่นแล้วเท่านั้น\"}},{\"id\":\"FiB7s8_gMV\",\"type\":\"header\",\"data\":{\"text\":\"วัตถุประสงค์ในการเก็บรวบรวมข้อมูลส่วนบุคคล\",\"level\":2}},{\"id\":\"raVTrGl-pL\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๘ &nbsp; เบต้าบล็อกเก็บรวบรวมข้อมูลส่วนบุคคล เพื่อการดำเนินงานของเบต้าบล็อกการศึกษา การวิจัย หรือการจัดทำสถิติซึ่งเป็นไปตามวัตถุประสงค์ของการดำเนินงานของเบต้าบล็อกและเพื่อปรับปรุงคุณภาพของการให้บริการของเบต้าบล็อกทั้งด้วยวิธีการปกติ หรือด้วยวิธีการทางอิเล็กทรอนิกส์ให้มีประสิทธิภาพมากยิ่งขึ้น\"}},{\"id\":\"vNgzFU3KGo\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๙ &nbsp; หากภายหลังมีการเปลี่ยนแปลงวัตถุประสงค์ในการเก็บรวบรวมข้อมูลส่วนบุคคล เบต้าบล็อกจะแจ้งให้เจ้าของข้อมูลส่วนบุคคลนั้นทราบและขอความยินยอมนั้นใหม่\"}},{\"id\":\"saoDrp95MO\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๐ &nbsp; ในกรณีที่เบต้าบล็อกมีการเก็บรวบรวม จัดเก็บ ใช้และเปิดเผยข้อมูลส่วนบุคคล เพื่อดำเนินการอื่นนอกเหนือจากวัตถุประสงค์ตามที่ได้ระบุไว้ เจ้าของข้อมูลส่วนบุคคลนั้นมีสิทธิที่ในความเป็นส่วนตัวของข้อมูลส่วนบุคคลในการเลือกว่า จะให้เบต้าบล็อกเก็บรวบรวม จัดเก็บ ใช้ เปิดเผยหรือไม่ให้เก็บรวบรวม จัดเก็บ ใช้ เปิดเผยข้อมูลส่วนบุคคลดังกล่าว\"}},{\"id\":\"NvvSoJ7fHh\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๑ &nbsp; เพื่อวัตถุประสงค์ในการวิเคราะห์และติดตามการให้บริการทางอิเล็กทรอนิกส์ การใช้บริการทางเว็บไซต์ หรือระบบออนไลน์อื่นใด และวัตถุประสงค์ในการตรวจสอบย้อนหลังในกรณีที่เกิดปัญหาการใช้งาน รวมถึงการปฏิบัติตามพระราชบัญญัติว่าด้วยการกระทำความผิดเกี่ยวกับคอมพิวเตอร์ และ พระราชบัญญัติการรักษาความมั่นคงปลอดภัยไซเบอร์ เบต้าบล็อกจึงจัดเก็บบันทึกข้อมูลการเข้าออกเว็บไซต์ (Log Files) โดยระบบอัตโนมัติ โดยจัดเก็บข้อมูลต่อไปนี้เป็นอย่างน้อย\"}},{\"id\":\"JMidyrI2MP\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๑) หมายเลขไอพี (IP Address)\"}},{\"id\":\"znbw-_NfgN\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๒) ประเภทของโปรแกรมบราวเซอร์ (Browser)\"}},{\"id\":\"I6qeus9_53\",\"type\":\"paragraph\",\"data\":{\"text\":\"นอกจากนี้ เบต้าบล็อกยังได้ใช้บริการของหน่วยงานภายนอกที่มีการจัดเก็บบันทึกการเข้าออกระบบให้บริการทางเว็บไซต์ตามที่กฎหมายกำหนดด้วย สำหรับกรณีที่เบต้าบล็อกใช้บริการหน่วยงานภายนอกในการให้บริการเว็บไซต์\"}},{\"id\":\"qhYXstD3rJ\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๒ &nbsp; เพื่อวัตถุประสงค์ของการรักษาความปลอดภัย การดูแลชีวิต สุขภาพ และทรัพย์สินของผู้ปฏิบัติงานในเบต้าบล็อกผู้ใช้บริการของเบต้าบล็อกและประชาชนทั่วไปที่ใช้พื้นที่เบต้าบล็อกเบต้าบล็อกจะจัดเก็บรวบรวมข้อมูลเพื่อการดังกล่าว ซึ่งอาจเชื่อมโยงไประบุตัวบุคคลได้ โดยจะเก็บรวบรวมด้วยวิธีการที่เหมาะสมเช่น กล้องวงจรปิด (CCTV) การประมวลผลภาพและเสียงและจัดเก็บในรูปแบบข้อมูลอิเล็กทรอนิกส์ เป็นต้น โดยข้อมูลดังกล่าวเบต้าบล็อกจะใช้อย่างจำกัด เฉพาะที่จำเป็นให้เป็นไปตามวัตถุประสงค์ หรือตามที่กฎหมายกำหนด ซึ่งหมายรวมถึงการประมวลผลเพื่อเพิ่มประสิทธิภาพการให้บริการ\"}},{\"id\":\"hipAtg3Q1z\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๓ &nbsp; เบต้าบล็อกจะไม่กระทำการใด ๆ แตกต่างจากที่ระบุในวัตถุประสงค์ของการเก็บรวบรวมข้อมูล เว้นแต่\"}},{\"id\":\"V26M7fyXLJ\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๑) ได้แจ้งวัตถุประสงค์ใหม่ให้ทราบแล้ว หรือได้รับความยินยอมจากเจ้าของข้อมูลส่วนบุคคลแล้ว\"}},{\"id\":\"6KKTeAXCO0\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๒) เป็นกรณีที่กฎหมายกำหนด\"}},{\"id\":\"8KJtFAGdXK\",\"type\":\"header\",\"data\":{\"text\":\"การนำข้อมูลส่วนบุคคลไปใช้อย่างจำกัด\",\"level\":2}},{\"id\":\"q5SdzkQDmd\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๔ &nbsp; เบต้าบล็อกจะใช้ เปิดเผยข้อมูลส่วนบุคคลได้ต่อเมื่อเป็นการใช้ตามวัตถุประสงค์ของเบต้าบล็อกเท่านั้น\"}},{\"id\":\"z9vKiB7w_Z\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๕ &nbsp; เบต้าบล็อกจะดูแลให้ผู้ปฏิบัติงานของเบต้าบล็อกมิให้เปิดเผย แสดง หรือทำให้ปรากฏในลักษณะอื่นใดซึ่งข้อมูลส่วนบุคคลนอกเหนือไปจากวัตถุประสงค์หรือต่อบุคคลภายนอก เว้นแต่\"}},{\"id\":\"pAUsRq5JrO\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๑) เป็นกรณีที่กฎหมายกำหนด\"}},{\"id\":\"UjV2GeM1GX\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๒) ได้รับความยินยอมจากเจ้าของข้อมูลส่วนบุคคล\"}},{\"id\":\"pJ8UVAaSmQ\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๓) เป็นไปเพื่อประโยชน์เกี่ยวกับชีวิต สุขภาพ หรือความปลอดภัยของเจ้าของข้อมูลส่วนบุคคลและผู้ใช้บริการอื่น\"}},{\"id\":\"OrnB9m_SwO\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๔) เพื่อประโยชน์แก่การสอบสวนของพนักงานสอบสวน หรือการพิจารณาพิพากษาคดีของศาล\"}},{\"id\":\"JcMn85NeO7\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๕) เพื่อประโยชน์ในการศึกษา วิจัย หรือการจัดทำสถิติ\"}},{\"id\":\"L_XRGZmUzu\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๖ &nbsp; ในบางกรณีเบต้าบล็อกอาจให้บุคคลหรือหน่วยงานอื่นเข้าถึงหรือใช้ข้อมูลส่วนบุคคลเท่าที่จำเป็น และเพื่อให้เป็นไปตามวัตถุประสงค์และอำนาจหน้าที่ของเบต้าบล็อก\"}},{\"id\":\"XnZJcPGTiC\",\"type\":\"header\",\"data\":{\"text\":\"การรักษาความมั่นคงปลอดภัย\",\"level\":2}},{\"id\":\"LXf1Y92lrM\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๗ &nbsp; เบต้าบล็อกตระหนักถึงความสำคัญของการรักษาความมั่นคงปลอดภัยของข้อมูลส่วนบุคคล เบต้าบล็อกจึงกำหนดให้มีมาตรการในการรักษาความมั่นคงปลอดภัยของข้อมูลส่วนบุคคลอย่างเหมาะสมและสอดคล้องกับการรักษาความลับของข้อมูลส่วนบุคคลเพื่อป้องกันการสูญหาย การเข้าถึง ทำลาย ใช้ แปลง แก้ไขหรือเปิดเผยข้อมูลส่วนบุคคลโดยไม่มีสิทธิหรือโดยไม่ชอบด้วยกฎหมาย โดยให้เป็นไปตามที่กำหนดในนโยบายและแนวปฏิบัติในการรักษาความมั่นคงปลอดภัยด้านเทคโนโลยีสารสนเทศของเบต้าบล็อก\"}},{\"id\":\"8JC6DjafE5\",\"type\":\"header\",\"data\":{\"text\":\"การมีส่วนร่วมของเจ้าของข้อมูลส่วนบุคคล\",\"level\":2}},{\"id\":\"2AJ9KISpee\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๘ &nbsp; ในกรณีที่เจ้าของข้อมูลส่วนบุคคลประสงค์จะทราบข้อมูลส่วนบุคคลเกี่ยวกับตนเอง สามารถมีคำร้องขอตามหลักเกณฑ์และวิธีการที่เบต้าบล็อกกำหนด เมื่อเบต้าบล็อกได้รับคำร้องขอดังกล่าวแล้ว เบต้าบล็อกจะรีบดำเนินการแจ้งถึงความมีอยู่ หรือรายละเอียดของข้อมูลส่วนบุคคลดังกล่าวให้ทราบภายในระยะเวลาอันสมควร\"}},{\"id\":\"h-a7J1ZGtD\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๑๙ &nbsp; หากเจ้าของข้อมูลส่วนบุคคลเห็นว่า ข้อมูลส่วนบุคคลใดที่เกี่ยวกับตนไม่ถูกต้องตามที่เป็นจริง สามารถแจ้งเบต้าบล็อกเพื่อให้แก้ไข เปลี่ยนแปลง หรือลบข้อมูลส่วนบุคคลนั้นได้ ในการนี้ เบต้าบล็อกจะจัดทำบันทึกคำคัดค้านการจัดเก็บ ความถูกต้อง หรือการกระทำใด ๆ เกี่ยวกับข้อมูลส่วนบุคคลนั้นไว้เป็นหลักฐานด้วย\"}},{\"id\":\"3t7gtjq_aS\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๒๐ &nbsp; เจ้าของข้อมูลส่วนบุคคลมีสิทธิตรวจดูความมีอยู่ ลักษณะของข้อมูลส่วนบุคคล วัตถุประสงค์ของการนำข้อมูลไปใช้ และสถานที่ทำการของเบต้าบล็อกนอกจากนี้ ยังมีสิทธิดังต่อไปนี้\"}},{\"id\":\"RX-vDUfmwu\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๑) ขอสำเนา หรือขอสำเนารับรองถูกต้องเกี่ยวกับข้อมูลส่วนบุคคลของตน\"}},{\"id\":\"_p4s_gvnvB\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๒) ขอแก้ไข หรือเปลี่ยนแปลงข้อมูลส่วนบุคคลของตนให้ถูกต้องสมบูรณ์\"}},{\"id\":\"2xM3tVfJ73\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๓) ขอระงับการใช้ หรือเปิดเผยข้อมูลส่วนบุคคลที่เกี่ยวกับตน\"}},{\"id\":\"UhXZUg5Yp_\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๔) ขอให้ดำเนินการลบ หรือทำลายข้อมูลส่วนบุคคลที่เกี่ยวกับตน\"}},{\"id\":\"M3B0feRl98\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๕) ขอให้เปิดเผยถึงการได้มาซึ่งข้อมูลส่วนบุคคลที่เกี่ยวกับตนในกรณีที่เป็นข้อมูลซึ่งผู้ใช้บริการไม่ได้ให้ความยินยอมในการรวบรวมหรือจัดเก็บ\"}},{\"id\":\"JYvHVvK0B9\",\"type\":\"paragraph\",\"data\":{\"text\":\"อย่างไรก็ตาม เบต้าบล็อกอาจปฏิเสธสิทธิได้ในกรณีที่กฎหมายกำหนด หรือในกรณีที่ข้อมูลส่วนบุคคลนั้นถูกทำให้ไม่ปรากฏชื่อหรือสิ่งบอกลักษณะอันสามารถระบุตัวบุคคลได้อีก\"}},{\"id\":\"mOm4jSRAiB\",\"type\":\"header\",\"data\":{\"text\":\"การเชื่อมโยงข้อมูลส่วนบุคคลกับบุคคลหรือหน่วยงานอื่น\",\"level\":2}},{\"id\":\"S4JBlAWT5h\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๒๑ &nbsp; เบต้าบล็อกอาจเชื่อมโยงข้อมูลส่วนบุคคลกับบุคคลหรือหน่วยงานอื่น เบต้าบล็อกจะแจ้งให้เจ้าของข้อมูลส่วนบุคคลทราบก่อนที่จะทำการเชื่อมโยงข้อมูลส่วนบุคคล พร้อมทั้งขอความยินยอม โดยมีรายละเอียดต่อไปนี้เป็นอย่างน้อย\"}},{\"id\":\"iO8J5ttRBn\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๑) บุคคลหรือหน่วยงานที่จะทำการเชื่อมโยงข้อมูลส่วนบุคคล\"}},{\"id\":\"Gs2UMEG_gO\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๒) วัตถุประสงค์ในการเชื่อมโยงข้อมูลส่วนบุคคล\"}},{\"id\":\"zK4-_VsfOp\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๓) วิธีการในการเชื่อมโยงข้อมูลส่วนบุคคล\"}},{\"id\":\"7ebPziWEXx\",\"type\":\"paragraph\",\"data\":{\"text\":\"(๔) ข้อมูลส่วนบุคคลที่จะทำการเชื่อมโยง\"}},{\"id\":\"8a76fpBvk3\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๒๒ &nbsp; ในการเชื่อมโยงข้อมูลส่วนบุคคลกับบุคคลหรือหน่วยงานอื่น เบต้าบล็อกจะแสดงชื่อผู้เก็บรวบรวม บุคคลผู้มีสิทธิในข้อมูลที่ได้มีการเก็บรวบรวมอย่างชัดเจน เพื่อให้เจ้าของข้อมูลส่วนบุคคลได้รับทราบ นอกจากนี้ เบต้าบล็อกจะจัดทำบันทึกการเชื่อมโยงข้อมูลไว้เป็นหลักฐาน\"}},{\"id\":\"CavcOf6u8h\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๒๓ &nbsp; หากมีการเปลี่ยนแปลงการเชื่อมโยงข้อมูล เบต้าบล็อกจะแจ้งให้เจ้าของข้อมูลส่วนบุคคลทราบถึงการเปลี่ยนแปลงดังกล่าว และขอความยินยอมก่อนการดำเนินการ\"}},{\"id\":\"1b_cI93kqK\",\"type\":\"header\",\"data\":{\"text\":\"การเปลี่ยนแปลงนโยบายข้อมูลส่วนบุคคล\",\"level\":2}},{\"id\":\"Kvd0HhoTa7\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๒๔ &nbsp; เบต้าบล็อกอาจปรับปรุงนโยบายส่วนบุคคลนี้เป็นครั้งคราวเพื่อให้สอดคล้องกับการเปลี่ยนแปลงของการให้บริการ การดำเนินงานของเบต้าบล็อกและข้อเสนอแนะความคิดเห็นจากเจ้าของข้อมูลส่วนบุคคล เบต้าบล็อกจะประกาศแจ้งการเปลี่ยนแปลงให้ทราบอย่างชัดเจนก่อนจะเริ่มดำเนินการเปลี่ยนแปลง หรืออาจส่งประกาศแจ้งเตือนให้ทราบโดยตรง\"}},{\"id\":\"H7CT0xVj_k\",\"type\":\"paragraph\",\"data\":{\"text\":\"ข้อ &nbsp;๒๕ &nbsp; สำหรับข้อมูลเพิ่มเติมเกี่ยวกับนโยบายและแนวปฏิบัติคุ้มครองข้อมูลส่วนบุคคล โปรดศึกษาจากนโยบายและแนวปฏิบัติคุ้มครองข้อมูลส่วนบุคคลฉบับนี้ หากมีข้อสงสัยเพิ่มเติม โปรดติดต่อเบต้าบล็อกที่\"}},{\"id\":\"6Io2Nbk3z7\",\"type\":\"paragraph\",\"data\":{\"text\":\"กองกฎหมาย เบต้าบล็อก\"}},{\"id\":\"_R8kfR07JC\",\"type\":\"paragraph\",\"data\":{\"text\":\"เว็บไซต์ <a href=\\\"http://blog.villsource.net\\\">blog.villsource.net</a>\"}}],\"version\":\"2.28.2\"} ",
            ContentPreviews = "นโยบายนี้อธิบายถึงวิธีการที่เบต้าบล็อกปฏิบัติต่อข้อมูลส่วนบุคคล ประกอบด้วยการเก็บรวบรวม ใช้ หรือเปิดเผยข้อมูลส่วนบุคคล ภายใต้หลักการคุ้มครองข้อมูลส่วนบุคคลไม่ให้มีการละเมิดสิทธิความเป็นส่วนตัวของบุคคล ซึ่งอาจก่อให้เกิดความเดือดร้อนรำคาญหรือความเสียหายให้แก่เจ้าของข้อมูลส่วนบุคคล แต่ยังคงไว้ซึ่งความจำเป็นในการใช้ประโยชน์จากข้อมูลส่วนบุคคลตามวัตถุประสงค์ของการเก็บรวบรวมข้อมูลส่วนบุคคล",
